import express from 'express';
import connectDB from './database.js';
import router from './routes/analyticsRoutes.js';

const app = express();

connectDB();

app.use(express.json());

app.use('/api', router);

const PORT = process.env.PORT || 3000;
app.listen(PORT, () => {
  console.log(`Server running on port ${PORT}`);
});
